import UIKit

protocol SearchMainPresentationLogic {
    func present(response: SearchMain.Repository.Response)
    func presentUserList(response: SearchMain.User.Response)
    func presentError()
}

class SearchMainPresenter: SearchMainPresentationLogic {
    weak var viewController: SearchMainDisplayLogic?
    
    // MARK: Do something
    
    func present(response: SearchMain.Repository.Response) {
        viewController?.display(response: response)
    }
    func presentUserList(response: SearchMain.User.Response) {
        viewController?.displayUserList(response: response)
    }
    
    func presentError() {
        
    }
}
